-- SQL Schema for Academic Audit Portal\n\nCREATE TABLE users (\n    user_id INT PRIMARY KEY AUTO_INCREMENT,\n    username VARCHAR(50) NOT NULL UNIQUE,\n    password_hash VARCHAR(255) NOT NULL,\n    email VARCHAR(100) NOT NULL UNIQUE,\n    created_at DATETIME DEFAULT CURRENT_TIMESTAMP\n);\n\nCREATE TABLE faculty (\n    faculty_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT NOT NULL,\n    department VARCHAR(100),\n    designation VARCHAR(50),\n    FOREIGN KEY (user_id) REFERENCES users(user_id)\n);\n\nCREATE TABLE teaching_records (\n    record_id INT PRIMARY KEY AUTO_INCREMENT,\n    faculty_id INT NOT NULL,\n    course_code VARCHAR(20),\n    semester VARCHAR(10),\n    year INT,\n    FOREIGN KEY (faculty_id) REFERENCES faculty(faculty_id)\n);\n\nCREATE TABLE research_data (\n    research_id INT PRIMARY KEY AUTO_INCREMENT,\n    faculty_id INT NOT NULL,\n    title VARCHAR(255),\n    publication_date DATE,\n    FOREIGN KEY (faculty_id) REFERENCES faculty(faculty_id)\n);\n\nCREATE TABLE institutional_contribution (\n    contribution_id INT PRIMARY KEY AUTO_INCREMENT,\n    faculty_id INT NOT NULL,\n    description TEXT,\n    contribution_date DATE,\n    FOREIGN KEY (faculty_id) REFERENCES faculty(faculty_id)\n);\n\nCREATE TABLE audit_documents (\n    document_id INT PRIMARY KEY AUTO_INCREMENT,\n    faculty_id INT NOT NULL,\n    file_path VARCHAR(255),\n    upload_date DATETIME DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (faculty_id) REFERENCES faculty(faculty_id)\n);\n\nCREATE TABLE notifications (\n    notification_id INT PRIMARY KEY AUTO_INCREMENT,\n    user_id INT NOT NULL,\n    message TEXT,\n    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,\n    FOREIGN KEY (user_id) REFERENCES users(user_id)\n);\n\nCREATE TABLE audit_cycles (\n    cycle_id INT PRIMARY KEY AUTO_INCREMENT,\n    start_date DATE,\n    end_date DATE\n);\n\nCREATE TABLE audit_logs (\n    log_id INT PRIMARY KEY AUTO_INCREMENT,\n    action_type VARCHAR(50),\n    action_description TEXT,\n    created_at DATETIME DEFAULT CURRENT_TIMESTAMP\n);\n